PREFIX cx: <https://raw.githubusercontent.com/catenax-ng/product-knowledge/main/ontology/cx_ontology.ttl#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT ?vehicle ?van ?adaption ?aggregate ?assembly ?supplier ?lc ?lcv ?operatingTime ?mileage ?recordDate ?healthType ?project ?body_count ?body_classes ?countUnit ?countMethod ?channels WHERE {

  VALUES (?van ?aggregate ?healthType) { 
      ("FNLQNRVCOFLHAQ"^^xsd:string "Differential Gear"^^xsd:string "Clutch"^^xsd:string) 
      ("FGPTXINYZAVJYK"^^xsd:string "Differential Gear"^^xsd:string "Clutch"^^xsd:string) 
      ("DVAJDTLJMKKZGY"^^xsd:string "Differential Gear"^^xsd:string "GearOil"^^xsd:string) 
      ("FKUBNDMDXQNFHI"^^xsd:string "Differential Gear"^^xsd:string "GearSet"^^xsd:string)  
  }

  ?vehicle rdf:type cx:Vehicle;
           cx:vehicleIdentificationNumber ?van.

  ?assembly rdf:type cx:AssemblyGroup;
            cx:partName ?aggregate;
            cx:isPartOf ?vehicle;
            cx:isProducedBy ?supplier;
            cx:hasLoadSpectrum ?lc.

  ?lc rdf:type cx:LoadSpectrum;
      cx:loadSpectrumDescription ?healthType;
      cx:vehicleOperatingHours ?operatingTime;
      cx:vehicleCurrentMileage ?mileage;
      cx:vehicleCurrentStateDateTime ?recordDate;
      cx:loadSpectrumType ?body_count;
      cx:hasLoadSpectrumValues [
      cx:loadSpectrumChannelIndex ?body_classes; 
      cx:loadSpectrumCountingUnit ?countUnit; 
      cx:loadSpectrumCountingMethod ?countMethod; 
      cx:loadSpectrumChannelValues ?channels ].

  OPTIONAL {
    ?lc cx:loadSpectrumName ?project
  }
}

PREFIX cx-common:   <https://www.w3id.org/catenax/ontology/common#>
PREFIX cx-core:     <https://www.w3id.org/catenax/ontology/core#>
PREFIX cx-vehicle:  <https://www.w3id.org/catenax/ontology/vehicle#>
PREFIX cx-vinfo:    <https://www.w3id.org/catenax/ontology/vehicle-information#>
PREFIX cx-part:     <https://www.w3id.org/catenax/ontology/part#>
PREFIX cx-load:     <https://www.w3id.org/catenax/ontology/loadspectrum#>
PREFIX cx-life:     <https://www.w3id.org/catenax/ontology/lifetime-prognosis#>
PREFIX rdf:         <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs:        <http://www.w3.org/2000/01/rdf-schema#>
PREFIX xsd:         <http://www.w3.org/2001/XMLSchema#>
PREFIX json:        <https://json-schema.org/draft/2020-12/schema#> 

SELECT ?vehicle ?van ?aggregate ?assembly ?supplier ?lc ?operatingTime ?mileage ?recordDate ?ls_type ?ls_name ?ls_value ?ls_unit ?ls_method ?ls_channels ?ls_classes ?ls_values ?distanceKm ?timeDays WHERE {

  VALUES (?van ?aggregate ?ls_type) { 
      ("FNLQNRVCOFLHAQ"^^xsd:string "Differential Gear"^^xsd:string "GearOil"^^xsd:string) 
  }

  SERVICE <{{oemProviderAgent5}}/sparql> {  
    ?vehicle rdf:type cx-vehicle:Vehicle;
           cx-vehicle:vehicleIdentificationNumber ?van;
           cx-part:partProductionDate ?productionDate.

     ?assembly rdf:type cx-part:AssemblyGroup;
             cx-part:partName ?aggregate;
             cx-part:isPartOf ?vehicle;
             cx-part:isProducedBy ?supplier;
             cx-load:hasLoadSpectrum ?lc.

     ?lc rdf:type cx-load:LoadSpectrum;
       cx-vinfo:vehicleOperatingHours ?operatingTime;
       cx-vinfo:vehicleCurrentMileage ?mileage;
       cx-vinfo:vehicleCurrentStateDateTime ?recordDate;
       cx-load:loadSpectrumId ?ls_type;
       cx-load:loadSpectrumName ?ls_name;
       cx-load:loadSpectrumValue ?ls_value;
       cx-load:loadSpectrumUnit ?ls_unit;
       cx-load:loadSpectrumMethod ?ls_method;
       cx-load:loadSpectrumChannels ?ls_channels;
       cx-load:loadSpectrumClasses ?ls_classes;
       cx-load:loadSpectrumValues ?ls_values.
  }

  SERVICE <{{tierARemotingAgent}}/repositories/rul> {
            ?invocation rdf:type cx-life:LifetimePrognosis;
                cx-life:loadSpectrumFile "{ \"Type\": \"ZF_load_collective\", \"Version\": \"1.7\" }"^^json:Object;
                cx-load:loadSpectrumMethod ?ls_method;
                cx-load:loadSpectrumUnit ?ls_unit;
                cx-load:loadSpectrumId ?ls_type;
                cx-load:loadSpectrumChannels ?ls_channels;
                cx-load:loadSpectrumClasses ?ls_classes;
                cx-load:loadSpectrumValues ?ls_values;
                cx-vehicle:vehicleIdentificationNumber ?van;
                cx-part:isProducedBy ?supplier;
                cx-vinfo:vehicleCurrentMileage ?mileage;
                cx-life:vehicleCurrentMileageUnit "km"^^xsd:string;
                cx-vinfo:vehicleRegistrationDate ?productionDate;
                cx-life:vehicleRegistrationDateFormat  "%Y-%m-%d"^^xsd:string;
                cx-life:remainingDistance ?distanceKm; 
                cx-life:remainingTime ?timeDays.
  } # SUPPLIER#CATALOG

}

PREFIX cx: <https://raw.githubusercontent.com/catenax-ng/product-knowledge/main/ontology/cx_ontology.ttl#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT * WHERE {

  VALUES (?van ?aggregate ?healthType) { 
      ("FNKQHZHFTHMCRX"^^xsd:string "Differential Gear"^^xsd:string "GearSet"^^xsd:string) 
      ("LKTYZWBNDOMPGQ"^^xsd:string "Differential Gear"^^xsd:string "GearSet"^^xsd:string) 
      ("GLKZNIGFNBJIHH"^^xsd:string "Differential Gear"^^xsd:string "GearOil"^^xsd:string) 
      ("FKUBNDMDXQNFHI"^^xsd:string "Differential Gear"^^xsd:string "GearOil"^^xsd:string)  
  }

  ?vehicle rdf:type cx:Vehicle;
           cx:vehicleIdentificationNumber ?van;
           ^cx:isPartOf ?assembly.

  ?assembly rdf:type cx:AssemblyGroup;
            cx:partName ?aggregate;
            cx:isProducedBy ?supplier;
            cx:hasLoadSpectrum ?lc.

  ?lc rdf:type cx:LoadSpectrum;
      cx:vehicleOperatingHours ?operatingTime;
      cx:vehicleCurrentMileage ?mileage;
      cx:vehicleCurrentStateDateTime ?recordDate;
      cx:loadSpectrumDescription ?healthType;
      cx:loadSpectrumName ?project;
      cx:loadSpectrumType ?body_count;
      cx:hasLoadSpectrumValues ?lsv.

  ?lsv rdf:type cx:LoadSpectrumValues;
      cx:loadSpectrumChannelIndex ?body_classes; 
      cx:loadSpectrumCountingUnit ?countUnit; 
      cx:loadSpectrumCountingMethod ?countMethod; 
      cx:loadSpectrumChannelValues ?channels.

}

PREFIX cx: <https://raw.githubusercontent.com/catenax-ng/product-knowledge/main/ontology/cx_ontology.ttl#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT ?materialNumber ?bpns ?part ?partName ?part2 ?part2Name ?part3 ?part3Name ?part4 ?part4Name ?part5 ?part5Name WHERE {

  VALUES (?materialClass ) { 
      ("OrganicMaterial_Rubber"^^xsd:string) 
  }

  ?material rdf:type cx:EngineeringMaterial;
     cx:engineeringMaterialNumber ?materialNumber.

  FILTER ( CONTAINS(?materialNumber,?materialClass)).

  ?part rdf:type cx:Part;
        cx:partName ?partName;
        cx:isMadeOf ?material;
        cx:isProducedBy ?bpns.

  ?part cx:isPartOf ?part2.
  ?part2 cx:partName ?part2Name.

  OPTIONAL {

   ?part2 cx:isPartOf ?part3.
   ?part3 cx:partName ?part3Name.

  }

  OPTIONAL {

    ?part3 cx:isPartOf ?part4.
    ?part4 cx:partName ?part4Name.

  }

  OPTIONAL {

    ?part4 cx:isPartOf ?part5.
    ?part5 cx:partName ?part5Name.

  }
}

PREFIX cx: <https://raw.githubusercontent.com/catenax-ng/product-knowledge/main/ontology/cx_ontology.ttl#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

JSON {
    "catenaXId": ?catenaXId,
    "partTypeInformation_nameAtManufacturer": ?partTypeInformation_nameAtManufacturer,
    "partTypeInformation_classification": "product",
    "partTypeInformation_manufacturerPartId": ?partTypeInformation_manufacturerPartId,
    "validityPeriod_validFrom": ?validatityPeriod_validFrom,
    "validityPeriod_validTo": ?validityPeriod_validTo
} WHERE {

  VALUES (?catenaXId) { 
      (<urn:uuid:0733946c-59c6-41ae-9570-cb43a6e4c79e>) 
  }

  SERVICE <{{oemProviderAgent3}}/sparql> {
    ?catenaXId rdf:type cx:Part;
        cx:partSeries ?partTypeInformation_manufacturerPartId;
        cx:partName ?partTypeInformation_nameAtManufacturer;
        cx:partProductionDate ?validatityPeriod_validFrom;
        cx:partProductionDateTime ?validityPeriod_validTo.
  }
}

PREFIX cx: <https://raw.githubusercontent.com/catenax-ng/product-knowledge/main/ontology/cx_ontology.ttl#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX : <:>

CONSTRUCT {
    [ :catenaXId ?twinId;
      :partTypeInformation [
          :nameAtManufacturer ?partName;
          :classification "product";
          :manufacturerPartId ?partSeries
      ];
      :validityPeriod [
          :validFrom ?from;
          :validTo ?to
      ]
    ].
} WHERE {

  VALUES (?twin) { 
      (<urn:uuid:0733946c-59c6-41ae-9570-cb43a6e4c79e>) 
  }

    ?twin rdf:type cx:Part;
        cx:partSeries ?partSeries;
        cx:partName ?partName;
        cx:partProductionDate ?from;
        cx:partProductionDate ?to.
}

PREFIX cx: <https://raw.githubusercontent.com/catenax-ng/product-knowledge/main/ontology/cx_ontology.ttl#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX : <:>

SELECT * WHERE {

  VALUES (?catenaXId) { 
      (<urn:uuid:0733946c-59c6-41ae-9570-cb43a6e4c79e>) 
  }

  ?catenaXId rdf:type cx:Part;
    cx:partSeries ?partTypeInformation_manufacturerPartId;
    cx:partName ?partTypeInformation_nameAtManufacturere;
    cx:partProductionDate ?validityPeriod_validFrom;
    cx:partProductionDate ?validityPeriod_validTo.

  BIND("product"^^xsd:string AS ?partTypeInformation_classification)
}

PREFIX cx: <https://raw.githubusercontent.com/catenax-ng/product-knowledge/main/ontology/cx_ontology.ttl#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT ?eMat
       ?engineeringMaterialName
       ?engineeringMaterialClass
       ?rMat
       ?componentState
       ?componentMaterialClass
       ?componentMaterialName
       ?componentRecycledContent
       ?componentMaterialAbbreviation
WHERE {
  SERVICE <{{oemProviderAgent3}}/sparql> {
    ?eMat cx:isMadeOf ?rMat;
        rdf:type cx:EngineeringMaterial;
        cx:engineeringMaterialName ?engineeringMaterialName;
        cx:engineeringMaterialNumber ?engineeringMaterialClass.
    ?rMat rdf:type cx:RawMaterial;
        cx:engineeringMaterialNumber ?componentMaterialClass;
        cx:hasMaterialProperty ?matProp;
        cx:engineeringMaterialName ?componentMaterialName.
    ?matProp #rdf:type cx:MaterialProperty;
        cx:aggregateState ?componentState;
        cx:recycledContent ?componentRecycledContent;
        cx:materialCode ?componentMaterialAbbreviation.
     }
}

