PREFIX cx: <https://raw.githubusercontent.com/catenax-ng/product-knowledge/main/ontology/cx_ontology.ttl#>
PREFIX cx-health: <https://github.com/catenax-ng/product-knowledge/ontology/health.ttl#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX json: <https://json-schema.org/draft/2020-12/schema#> 
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#> 

# Sample for a Federated Skill (No Matchmaking) Which
#  - Jumps right into the OEM with a set of VANs and aggregates
#  - Feeds this data into the respective tiera connector/agent to perform a health prognosis 

SELECT ?van ?aggregate ?supplier ?assembly ?healthType ?recordDate ?mileage ?operatingTime ?health ?supplierEDC ?healthIndicatorAsset WHERE {

    # Actual input parameters, when used as a predefined skill, 
    VALUES (?van ?aggregate ?healthType ?adaptionValues) { 
      ("@van"^^xsd:string "@aggregate"^^xsd:string "@healthType"^^xsd:string "@adaptionValues"^^json:Object) 
    }

    # Now we are jumping into the OEM
    SERVICE <{{oemProviderAgent2}}/sparql> { 

        ?vehicle rdf:type cx:Vehicle;
           cx:vehicleIdentificationNumber ?van.

        ?assembly rdf:type cx:AssemblyGroup;
            cx:partName ?aggregate;
            cx:isPartOf ?vehicle;
            cx:isProducedBy ?supplier;
            cx:hasLoadSpectrum [ 
                 cx:vehicleOperatingHours ?operatingTime;
                 cx:vehicleCurrentMileage ?mileage;
                 cx:vehicleCurrentStateDateTime ?recordDate;
                 cx:loadSpectrumDescription ?healthType;
                 cx:loadSpectrumType ?channels;
                 cx:hasLoadSpectrumValues [ 
                    cx:loadSpectrumCountingUnit ?countUnit; 
                    cx:loadSpectrumCountingMethod ?countMethod; 
                    cx:loadSpectrumChannelIndex ?classes; 
                    cx:loadSpectrumChannelValues ?counts
                 ]
            ].
    }

    ?supplier <https://github.com/catenax-ng/product-knowledge/ontology/cx.ttl#hasConnector> ?supplierEDC.
    ?supplierEDC <https://github.com/catenax-ng/product-knowledge/ontology/cx.ttl#offersAsset> ?healthIndicatorAsset.
    ?healthIndicatorAsset a cx:GraphAsset;
       rdfs:isDefinedBy <{{cxOntologyRoot}}/load_spectum_ontology.ttl>.

    #SERVICE <{{tierARemotingAgent}}/repositories/health> {
    SERVICE ?supplierEDC {
        GRAPH ?healthIndicatorAsset { 
            ?invocation rdf:type cx-health:HealthIndication;
                  cx-health:requestComponentId ?assembly;
                  cx-health:classifiedLoadCollectiveComponentDescription ?healthType;
                  cx-health:classifiedLoadCollectiveCountingValue ?countValue;
                  cx-health:classifiedLoadCollectiveCountingUnit ?countUnit;
                  cx-health:classifiedLoadCollectiveCountingMethod ?countMethod;
                  cx-health:classifiedLoadCollectiveChannels ?channels;
                  cx-health:classifiedLoadCollectiveClasses ?classes;
                  cx-health:classifiedLoadCollectiveCounts ?counts;
                  cx-health:adaptionValueVersion "0.6.4"^^xsd:string; 
                  cx-health:adaptionValueTimestamp ?recordDate;
                  cx-health:adaptionValueMileage ?mileage; 
                  cx-health:adaptionValueOperatingTime ?operatingTime;
                  cx-health:adaptionValueList ?adaptionValues;
                  cx-health:indicatorVersion ?indicatorVersion;
                  cx-health:responseComponentId ?indicatorComponentId;
                  cx-health:healthIndicatorValues ?health.    
        }
    }

}

PREFIX cx: <https://raw.githubusercontent.com/catenax-ng/product-knowledge/main/ontology/cx_ontology.ttl#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX json: <https://json-schema.org/draft/2020-12/schema#> 
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#> 

# Sample for a Federated Modular Skill (No Matchmaking) Which
#  - Jumps right into the OEM with a set of VINs
#  - Calls subskills to perform a health prognosis for the main aggregates 

SELECT ?van ?aggregate ?supplier ?assembly ?healthType ?health ?supplierEDC WHERE {

    # Actual input parameters, when used as a predefined skill, 
    VALUES (?vin ) { 
      ("@vin"^^xsd:string) 
    }

    # Lookup the manufacturer and its main vehicle information asset
    ?vin cx:worldManufacturerId ?oem.
    ?oem cx:hasConnector ?oemEDC.
    ?oemEDC cx:offersAsset ?vehicleAsset.
    ?vehicleAsset rdf:type cx:GraphAsset;
       rdfs:isDefinedBy <https://raw.githubusercontent.com/catenax-ng/product-knowledge/main/ontology/vehicle_information_ontology.ttl>.

    # Now we are jumping into the OEM
    SERVICE ?oemEDC { 

        GRAPH ?vehicleAsset {
            ?van rdf:type cx:Vehicle;
                cx:vehicleIdentificationNumber ?vin.

            ?assembly rdf:type cx:AssemblyGroup;
                cx:partName ?aggregate;
                cx:isPartOf ?van;
                cx:isProducedBy ?supplier.
        }

        ?telematicsAsset rdf:type cx:GraphAsset;
            rdfs:isDefinedBy <https://raw.githubusercontent.com/catenax-ng/product-knowledge/main/ontology/load_spectrum_ontology.ttl>.

        ?supplier cx:hasConnector ?supplierEDC.
        ?supplierEDC cx:offersAsset ?prognosisSkill.
        ?prognosisSkill a cx:SkillAsset;
            rdfs:isDefinedBy <https://raw.githubusercontent.com/catenax-ng/product-knowledge/main/ontology/prognosis_ontology.ttl>.

        SERVICE <{?supplierEDC}#{?prognosisSkill}> {
            BIND(?assembly AS ?assembly).
            BIND(?oemEDC as ?oem).
        }
    }

}

PREFIX cx: <https://raw.githubusercontent.com/catenax-ng/product-knowledge/main/ontology/cx_ontology.ttl#>
PREFIX cx-health: <https://github.com/catenax-ng/product-knowledge/ontology/health.ttl#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX json: <https://json-schema.org/draft/2020-12/schema#> 
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#> 

# Sample for a Modular Subskill
#  - Jumps back into the given OEM to obtain specific data
#  - Feeds this data into the health prognosis asset 

SELECT ?healthType ?health WHERE {

    # Actual input parameters, when used as a predefined skill, 
    VALUES (?assembly ?telematicsAsset ?oemEDC ?healthType) { 
      (<@assembly> <@asset> <@oem> "Gearbox"^^xsd:string) 
    }

    # Now we are jumping into the OEM
    SERVICE ?oemEDC { 
        GRAPH ?telematicsAsset {
        ?assembly rdf:type cx:AssemblyGroup;
            cx:hasLoadSpectrum [ 
