             cx:isProducedBy ?site2.

      OPTIONAL {
          ?part2 cx:isPartOf ?part3.
          ?part3 cx:partName ?part3Name;
                 cx:isProducedBy ?site3.
      }

     OPTIONAL {
          ?part3 cx:isPartOf ?part4.
          ?part4 cx:partName ?part4Name;
                 cx:isProducedBy ?site4.
     }

