PREFIX cx: <https://raw.githubusercontent.com/catenax-ng/product-knowledge/main/ontology/cx_ontology.ttl#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#> 

###############################
# Material Trace Skill Backed by the Product Trace Skill
# Input: 
#  incidentSite    - IRI to an incident business site
#  incidentMaterial- name of an affected material
#. maxDepth        - maximal search depth   
# Output:
#  affectedDepth. - The depth at which an affected end product has been detected
#  affectedSite.  - The site which produces the affected end product 
###############################

SELECT DISTINCT ?affectedDepth ?affectedSite WHERE {

  # Input parameter binding
  VALUES (?incidentSite ?incidentMaterial ?maxDepth) { 
      (<@incidentSite> "@incidentMaterial"^^xsd:string "@maxDepth"^^xsd:integer)
  }.

  # Exclude unplausible input  
  FILTER(?maxDepth >= 0).

  # Lookup the Traceability-As-Planned Graph/Asset of the relevant business partner
  ?incidentOrga cx:hasSite ?incidentSite;
                cx:hasConnector ?connectorUrl.

  # Call the TAP GrapH
  SERVICE ?connectorUrl {
      ?incidentProduct rdf:type cx:Part;
                    cx:partName ?partName;
                cx:isProducedBy ?incidentSite.

      FILTER( CONTAINS(?partName, ?incidentMaterial)).

      # If this is not end product, we find a consuming site/assembly
      OPTIONAL {
          ?incidentProduct cx:isPartOf ?assembly.
          ?assembly cx:isProducedBy ?site2.
      }
  }

  # Case disctinction
  {
     # The actual recursive call invokes this skill for all parent assemblies and moves the depth bounds
     FILTER(BOUND(?site2)).
     FILTER(?maxDepth >= 1).

     # Invoke the (recursive) product trace skill
     SERVICE <{{consumerAgentPlaneInternal}}/api/agent?asset=urn:cx:Skill:consumer:ProductTrace> {
        BIND(?maxDepth AS ?maxDepth).
        BIND(?site2 AS ?currentSite).
        BIND(?assembly AS ?currentProduct).
        BIND("1"^^xsd:integer AS ?currentDepth).
     }.
   } UNION {
       # The recursion ends at final products with no B2B consumers
       FILTER(!BOUND(?site2)).
       BIND("0"^^xsd:integer AS ?affectedDepth).
       BIND(?incidentSite AS ?affectedSite).
   }
}

PREFIX cx: <https://raw.githubusercontent.com/catenax-ng/product-knowledge/main/ontology/cx_ontology.ttl#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#> 
PREFIX skos: <http://www.w3.org/2004/02/skos/core#> 

SELECT ?material ?materialName ?isLeaf WHERE {
    VALUES (?parentMaterial ?lang) { 
        (<@parentMaterial> "@lang"^^xsd:string) 
    }
    OPTIONAL {
        ?material rdfs:subClassOf ?parentMaterial.
        BIND("false"^^xsd:boolean AS ?isLeaf).
    }
    OPTIONAL {
        ?material rdf:type ?parentMaterial.         
        BIND("true"^^xsd:boolean AS ?isLeaf).
    }

    ?material skos:prefLabel ?materialName.
    FILTER langMatches( lang(?materialName), ?lang )
}

