SELECT ?connector ?asset 
       ?name
       (MIN(?adescription) AS ?description) 
       ?type
       (MAX(?aversion) AS ?version) 
       (GROUP_CONCAT(DISTINCT ?acontentType;SEPARATOR=",") AS ?contentType) 
       (CONCAT("<",GROUP_CONCAT(?aontology;SEPARATOR=">,<"),">") AS ?isDefinedBy) 
       (MIN(?ashape) AS ?shape)
 WHERE { 
     ?connector <https://github.com/catenax-ng/product-knowledge/ontology/cx.ttl#offersAsset> ?asset. 
     ?asset <https://github.com/catenax-ng/product-knowledge/ontology/common_ontology.ttl#contentType> ?acontentType; 
            <https://github.com/catenax-ng/product-knowledge/ontology/common_ontology.ttl#name> ?name; 
            <http://www.w3.org/1999/02/22-rdf-syntax-ns#type> ?type; 
            <https://github.com/catenax-ng/product-knowledge/ontology/common_ontology.ttl#version> ?aversion; 
            <https://github.com/catenax-ng/product-knowledge/ontology/common_ontology.ttl#description> ?adescription.

    OPTIONAL {
        ?asset <http://www.w3.org/2000/01/rdf-schema#isDefinedBy> ?aontology; 
               <https://github.com/catenax-ng/product-knowledge/ontology/cx.ttl#shape> ?ashape
    }
} GROUP BY ?connector ?asset ?name ?type

