PREFIX cx: <https://github.com/catenax-ng/product-knowledge/ontology/cx.ttl#>
PREFIX cx-diag: <https://github.com/catenax-ng/product-knowledge/ontology/diagnosis.ttl#>
PREFIX cx-lifetime: <https://github.com/catenax-ng/product-knowledge/ontology/lifetime.ttl#>
PREFIX json: <https://json-schema.org/draft/2020-12/schema#> 
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#> 
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

#
# Sample for a Federated Skill (No Matchmaking) Which
#  - Uses an OEM connector/agent to infer diagnosis data 
#  - Feeds this data into a Tier A connector/agent to perform a prognosis 

SELECT ?vin ?troubleCode ?description ?partProg ?distance ?time WHERE {

    # Actual input parameters, when used as a predefined skill, 
    # these would be the named URL (or body multi-form) parameters
    # Since you could use them multiple times, there is a combinatorial difference in 
    # defining them as single variables or as combined tuples
    VALUES (?vin ?troubleCode) { ("WVA8984323420333"^^xsd:string "P0745"^^xsd:string) }

    # In this place, there would be a to-be-implemented catalogue logic 
    # (so the default "asset" would be the catalogue including the CX ontology)
    # which maps the VIN via regular expressions to the right 
    # business partner and connector
    # Currently, we chose even not the connector, but the backend directly
    VALUES (?oemBpn ?oemConnector) { ("BPNL00000003COJN"^^xsd:string <{{oemProviderAgent}}/sparql>) }

    # Now we are jumping into the OEM
    SERVICE ?oemConnector { 
        # Select Business Partner Node
        ?Oem cx:BPNL ?oemBpn.

        # Unless we have vehicle master data, we directly bind the variables
        VALUES (?mileage ?registrationDate) { (
            "{ \"Value\": 82563, \"Unit\": \"km\" }"^^json:Object
            "{ \"Value\": 20171206,  \"Unit\": \"yyyymmdd\" }"^^json:Object
        )}

        # Search for troubled parts to forecast
        ?Dtc rdf:type cx-diag:DTC;
            cx:provisionedBy ?Oem;
            cx-diag:PossibleCauses ?reason;
            cx-diag:Code ?troubleCode;
            cx-diag:Description ?description.

        # Unless we have a mapping of diagnosed parts to the prognosed parts
        VALUES ?partProg { "GearOil"^^xsd:string }        

        # Next we need to find the appropriate load spectrum associated to the prognosed part
        VALUES ?loadCollective { 
         "{ \"Temp_Oil-class\": [1, 3, 4, 5, 7, 8, 12, 13, 17, 18, 20, 22, 23, 25, 28, 29, 31, 32, 33, 34, 36, 37, 38, 41, 42, 44, 45, 47, 48, 49, 50, 51],  \"Counts\": [7.234E+01, 2.345E+02, 6.654E+02, 5.983E+01, 4.321E+02, 3.876E+02, 5.567E+01, 3.4456E+02, 4.556645E+02, 5.678E+01, 4.321E+02, 6.098E+02, 7.432E+02, 8.873E+02, 4.738E+01, 6.927E+01, 1.234E+02, 2.345E+02, 3.654E+02, 2.983E+01, 4.321E+01, 3.876E+02, 5.567E+02, 3.4456E+02, 4.556645E+02, 5.678E+01, 4.321E+01, 6.098E+01, 3.432E+02, 1.873E+02, 4.738E+02, 6.927E+02] }"^^json:Object
        }

        # we need to find the right supplier for the trouble part
        VALUES (?tieraBpn ?tieraConnector) {
           ("BPNL00000003CPIY"^^xsd:string <{{tierARemotingAgent}}/repositories/prognosis>) 
        }       

    }

    # Now we are jumping into the tiera
    # Notice that ideally, the OEM agent would perform this
    # as the consumer will have most likely no agreement/contract
    # for this purpose this SERVICE section would be nested
    # into the former section
    SERVICE ?tieraConnector { 
        ?invocation a cx-lifetime:LifetimePrognosis;
            # Input from the OEM
            cx-lifetime:loadCollectiveBody ?loadCollective;
            cx-lifetime:loadCollectiveComponent ?partProg;
            cx-lifetime:loadCollectiveMileage ?mileage;
            cx-lifetime:loadCollectiveRegistrationDate ?registrationDate;
            # Additional info from the Tiera about its measurements
            cx-lifetime:loadCollectiveFile "{  \"Type\": \"ZF_load_collective\", \"Version\": \"1.7\"  }"^^json:Object; 
            cx-lifetime:loadCollectiveHeader "{  \"CountingMethod\": \"ZF_TimeAtLevel\",    \"CountingUnit\": \"s\",    \"Channels\": [      {        \"Name\": \"Temp_Oil\",        \"Type\": \"Load\",        \"Unit\": \"degC\",        \"LowerLimit\": -40,        \"UpperLimit\": 220,        \"NumberOfBins\": 52      }  ]  }"^^json:Object; 
            # the actual prognosis output
            cx-lifetime:remainingDistance ?distance; 
            cx-lifetime:remainingTime ?time.
    }


}

PREFIX xsd:           <http://www.w3.org/2001/XMLSchema#> 
PREFIX json:          <https://json-schema.org/draft/2020-12/schema#>
PREFIX rdf:           <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs:          <http://www.w3.org/2000/01/rdf-schema#>
PREFIX cx:            <https://github.com/catenax-ng/product-knowledge/ontology/cx.ttl#>
PREFIX cx-diag:       <https://github.com/catenax-ng/product-knowledge/ontology/diagnosis.ttl#>
PREFIX cx-telematics: <https://github.com/catenax-ng/product-knowledge/ontology/telematics.ttl#>
PREFIX cx-health: <https://github.com/catenax-ng/product-knowledge/ontology/health.ttl#>

##############################################################################################
#                  Catena-X Knowledge Agents Sample Federated Skill                          #
#                         Realizes a 5-Step Business Process                                 #
#                            "Health Indicator Prognosis"                                    #
##############################################################################################
# Preconditions:                                                                             #
# - A Contract Offering from OEM (e.g. BMW) to CONSUMER (e.g. ADAC)                          #
#   - VIN-VAN Conversion                                                                     #
#   - Serial Part & SUPPLIER Lookup                                                          #
# - A Contract Offering from SUPPLIER (e.g. ZF) to OEM                                       #
#   - Telematics data (including the PROPAGATION of LoadSpectrum)                            #
#   - HI Prognosis Invocation (including the DISTRIBUTION of RUL results)                   #
##############################################################################################

####
# 5. Project the actual output of the Skill on CONSUMER side
####
SELECT ?vin ?van ?indicatorComponentId ?health WHERE {

    ####
    # 1. The CONSUMER detects a trouble code on a car in his fleet
    ####
    VALUES (?vin ?partType) { ("WBAAL31029PZ00002"^^xsd:string "clutch"^^xsd:string) }.

    ####
    # 2. The CONSUMER looks up the OEM (connector) associated to the VIN 
    #    using the Federated Data Catalogue  (Catalogue=Default Graph)
    ####
    ?oem cx:isIssuerOfVehicleIdentificationNumber ?vin;
         cx:hasConnector ?oemConnector.

    ?oemConnector cx:offersAsset ?diagnoseAsset.
    ?diagnoseAsset rdfs:isDefinedBy "<https://raw.githubusercontent.com/catenax-ng/product-knowledge/main/ontology/diagnosis_ontology.ttl>"^^xsd:string.

    ####
    # 3. The CONSUMER delegates the following logic to the OEM (connector)
    ####
    SERVICE ?oemConnector { 

        ####
        # 3.1 The OEM (e.g. BMW) anomyzes the VIN into an anomymous (VAN) node
        #.    and gets some master data with it 
        ####
        ?van cx:isAnonymousVehicle ?vin;
             cx:hasRegistration ?registration.
        GRAPH ?diagnoseAsset {    
        } # OEM#Diagnosis context

        ####
        # 3.2 The OEM obtains fresh telematics/load-spectrum data for the vehicle
        #     focussed to the problematic partType (Telematics Graph) 
        ####
        ?van cx-telematics:latestMileage ?mileage;
             cx-telematics:operatingTime ?operatingTime;
             cx-telematics:latestDetailReceived ?telematicsDetail.

        ?telematicsDetail cx-diag:hasPartType ?partType;
                          cx-diag:whenRecorded ?recordedTime;
                          cx-diag:hasAdaptionValues ?adaptionValues;
                          cx-diag:hasLoadSpectrum ?loadSpectrum.

        ####
        # 3.3 The OEM looks up the serialized part of the VAN (Traceability Graph)
        #     and the supplier address in the dataspace
        ####
        ?serializedPart cx:isComponentOf+ ?van;
                        cx:hasPartType ?partType;
                        cx:hasSupplier [
                            cx:hasConnector ?tieraConnector
                        ].           

        ?tieraConnector cx:offersAsset ?healthAsset.
        ?healthAsset rdfs:isDefinedBy "<https://raw.githubusercontent.com/catenax-ng/product-knowledge/main/ontology/load_spectrum_ontology.ttl>"^^xsd:string.

        ####
        # 4. The OEM (and not the CONSUMER) delegates to the SUPPLIER (connector)
        #    which means that load spectrum data etc is only exchanged using their
        #    contract and between their connectors.
        ####
        SERVICE ?tieraConnector { 

            ####
            # 4.1 The SUPPLIER adds additional measurement information
            ####
            ?telematicsDetail cx-telematics:hasFile ?loadSpectrumFile;
                              cx-telematics:hasAdaptionVersion ?adaptionVersion;
                              cx-telematics:hasHeader ?loadSpectrumHeader.

            ####
            # 4.2 The SUPPLIER invokes a prognosis model associated the part type using the load-spectrum data
            ####
            GRAPH ?healthAsset {
                ?invocation rdf:type cx-health:HealthIndication;
                                     cx-health:requestComponentId ?serializedPart;
                                     cx-health:classifiedLoadCollectiveFile ?loadSpectrumFile;
                                     cx-health:classifiedLoadCollectiveHeader ?loadSpectrumHeader;
                                     cx-health:classifiedLoadCollectiveBody ?loadSpectrum;
                                     cx-health:adaptionValueVersion ?adaptionVersion; 
                                     cx-health:adaptionValueTimestamp ?recordedTime;
                                     cx-health:adaptionValueMileage ?mileage; 
                                     cx-health:adaptionValueOperatingTime ?operatingTime;
                                     cx-health:adaptionValueList ?adaptionValues;
                                     cx-health:indicatorVersion ?indicatorVersion;
                                     cx-health:responseComponentId ?indicatorComponentId;
                                     cx-health:healthIndicatorValues ?health.

            } # SUPPLIER#Prognosis context

        } # SUPPLIER context

    } # OEM context

}

