PREFIX cx: <https://raw.githubusercontent.com/catenax-ng/product-knowledge/main/ontology/cx_ontology.ttl#>
PREFIX cx-health: <https://raw.githubusercontent.com/catenax-ng/product-knowledge/main/ontology/vehicle_information_ontology.ttl#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX json: <https://json-schema.org/draft/2020-12/schema#> 
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#> 

##########################################################
# Sample for a Federated (OEM-Deployed) SparQL Skill which
#  - Jumps right into self-owned telematics assets given a set of candidate VANs, assembly groups and load spectrum types
#  - Feeds the gathered data into the respective supplier connector/agent to perform a health indication 
# Author: cgjung
# (c) 2023 Catena-X assocation
##########################################################

SELECT ?van ?aggregate ?assembly ?healthType ?recordDate ?mileage ?operatingTime ?health WHERE {

    ###
    # Input parameter declaration 
    ###
    VALUES (?van ?aggregate ?healthType) { 
      ("@van"^^xsd:string "@aggregate"^^xsd:string "@healthType"^^xsd:string) 
    }

    ###
    # This is our own asset; we could also use our (provider) EDC address (requires a second consuming EDC instance)
    ###
    SERVICE <{{oemProviderAgent2}}/sparql> { 

        ###
        # Lookup vehicle and current telematics header
        ###
        ?vehicle rdf:type cx:Vehicle;
           cx:vehicleIdentificationNumber ?van;
           cx:hasAdaption [
                cx:vehicleOperatingHours ?operatingTime;
                cx:vehicleCurrentMileage ?mileage;
                cx:vehicleCurrentStateDateTime ?recordDate;
                cx:hasValues ?adaptionValues
           ].

        ###
        # Level-1 Down Traceability to Assembly Groups
        ###
        ?assembly rdf:type cx:AssemblyGroup;
            cx:partName ?aggregate;
            cx:isPartOf ?vehicle;
            cx:isProducedBy ?supplier;
            # Intersect Assembly Group with Telematics Details to get the right load spectrum
            cx:hasLoadSpectrum [ rdf:type cx:LoadSpectrum;
                cx:loadSpectrumDescription ?healthType;
                cx:loadSpectrumType ?channels;

                ###
                # and finally obtain the actual measurements (>50kb per instance, aware your web server settings)
                ###
                cx:hasLoadSpectrumValues [
                    cx:loadSpectrumChannelIndex ?classes; 
                    cx:loadSpectrumCountingUnit ?countUnit; 
                    cx:loadSpectrumCountingMethod ?countMethod; 
                    cx:loadSpectrumChannelValues ?counts 
                ]
            ].
    }

    ###
    # Lookup the federated data catalogue (per supplier)
    ###
    ?supplier cx:hasConnector ?supplierEDC;
              cx:offers ?healthIndicatorAsset.
    ?healthIndicatorAsset rdfs:isDefinedBy cx-health:.

    ###
    # The following will initiate (parallel) EDC negotations and delegate
    # through the data planes right into the binding agents
    # of the respective suppliers
    ###
    SERVICE ?supplierEDC { 
        GRAPH ?healthIndicatorAsset {

            ###
            # The invocation node stands for a batch of final API calls
            ###
            ?invocation rdf:type cx:HealthIndication;
                        ###
                        # The input nodes will be rewritten into the requests
                        ###
                        cx:requestComponentId ?assembly;
                        cx:classifiedLoadCollectiveComponentDescription ?healthType;
                        cx:classifiedLoadCollectiveCountingValue ?countValue;
                        cx:classifiedLoadCollectiveCountingUnit ?countUnit;
                        cx:classifiedLoadCollectiveCountingMethod ?countMethod;
                        cx:classifiedLoadCollectiveChannels ?channels;
                        cx:classifiedLoadCollectiveClasses ?classes;
                        cx:classifiedLoadCollectiveCounts ?counts;
                        cx:adaptionValueVersion "0.6.4"^^xsd:string; 
                        cx:adaptionValueTimestamp ?recordDate;
                        cx:adaptionValueMileage ?mileage; 
                        cx:adaptionValueOperatingTime ?operatingTime;
                        cx:adaptionValueList ?adaptionValues;
                        ###
                        # The output nodes will be extracted from the responses
                        ###
                        cx:indicatorVersion ?indicatorVersion;
                        cx:responseComponentId ?indicatorComponentId;
                        cx:healthIndicatorValues ?health.    
        } # healthIndicatorAsset
    } # supplierEDC
} # SELECT
